import React from 'react';
import { motion } from 'framer-motion';

const Members = [
  {
    name: "Younes TAOUMI ",
    role: "Team Leader",
    image: "/images/younes.jpg", // remplace avec ton image
  },
  {
    name: "Maryam BADRI",
    role: "Vice Team Leader",
    image: "/images/maryam.jpg",
  },
  {
    name: "Wiam ABOUTARA BELRHITI",
    role: "Vice President Project",
    image: "/images/wiam.jpg",
  },
   {
     name: "Ammar MASSAOUDI",
    role: "Communication Manager",
    image: "/images/ammar.jpg",
  },
    {
      name: "Hind ZAKY",
    role: "Human Ressources Manager",
    image: "/images/hind.jpg",
 },
];

const Accueil = () => {
  return (
    <>
      {/* Vidéo en fond */}
      <div>
        <video autoPlay loop muted playsInline className="background-video">
          <source src={require('../images/Enactus.mp4')} type="video/mp4" />
        </video>

        {/* Section équipe */}
        <section className="equipe-section">
          <h2 className="equipe-titre">Notre Équipe</h2>
          <div className="equipe-membres">
            {members.map((member, index) => (
              <motion.div
                key={index}
                className="membre-carte"
                whileHover={{ scale: 1.05 }}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.5, delay: index * 0.2 }}
              >
                <img src={member.image} alt={member.name} className="membre-photo" />
                <h3 className="membre-nom">{member.name}</h3>
                <p className="membre-role">{member.role}</p>
              </motion.div>
            ))}
          </div>
        </section>



      </div>
    </>
  );
};


export default Memberes;
