import React, { useRef } from 'react';
import '../style/Apropos.css';

function Apropos() {
  const carouselRef = useRef(null);

  const scrollCarousel = (direction) => {
    const container = carouselRef.current;
    const scrollAmount = 300;
    if (direction === 'left') {
      container.scrollLeft -= scrollAmount;
    } else {
      container.scrollLeft += scrollAmount;
    }
  };

  return (
    <section >
      <h2><strong>
        
        
        
        
        
        
        À propos de nous</strong></h2>
      <p> Depuis 2014Enactus FSBM rassemble des étudiants passionnés par l’entrepreneuriat social.
              Ensemble, nous avons réalisé <strong>40+</strong> projets, mobilisé <strong>1000+</strong> étudiants
              et transformé la vie de <strong>500+</strong> bénéficiaires.
            </p>

      <div className="carousel-wrapper">
        <button className="nav left" onClick={() => scrollCarousel("left")} aria-label="Gauche">
          &lt;
        </button>


        <div className="carousel-container" ref={carouselRef}>
         

          <div className="product-card" tabIndex="0">
            <h3>Top 12</h3>
            <p>Classement national 2023-2024</p>
          </div>

          <div className="product-card" tabIndex="0">
            <h3>13</h3>
            <p>ODD touchés</p>
          </div>

          <div className="product-card" tabIndex="0">
            <h3>3000h+</h3>
            <p>Heures de formation</p>
          </div>

          <div className="product-card" tabIndex="0">
            <h3>40+</h3>
            <p>Projets réalisés</p>
          </div>

          <div className="product-card" tabIndex="0">
            <h3>300+</h3>
            <p>Entreprises créées</p>
          </div>

          <div className="product-card" tabIndex="0">
            <h3>10 000</h3>
            <p>Jeunes mobilisés</p>
          </div>

          <div className="product-card" tabIndex="0">
            <h3>120</h3>
            <p>Établissements univers affiliés</p>
          </div>
        </div>

        <button className="nav right" onClick={() => scrollCarousel("right")} aria-label="Droite">
          &gt;
        </button>
      </div>

      <p className="formule">
        Enactus = <span>Entrepreneuriat</span> + <span>Action</span> + <span>Us</span>
      </p>
    </section>
  );
}

export default Apropos;
