import React from 'react';
import { motion } from 'framer-motion';

const members = [
  {
    name: "Younes TAOUMI ",
    role: "Team Leader",
    image: "/images/younes.jpg", // remplace avec ton image
  },
  {
    name: "Maryam BADRI",
    role: "Vice Team Leader",
    image: "/images/maryam.jpg",
  },
  {
    name: "Wiam ABOUTARA BELRHITI",
    role: "Vice President Project",
    image: "/images/wiam.jpg",
  },
   {
     name: "Ammar MASSAOUDI",
    role: "Communication Manager",
    image: "/images/ammar.jpg",
  },
    {name: "Hind ZAKY",
    role: "Human Ressources Manager",
    image: "/images/hind.jpg",
 },
];

const TeamSection = () => {
  return (
    <section className="bg-gradient-to-b from-blue-100 to-white py-16 px-4">
      <div className="max-w-6xl mx-auto text-center">
        <h2 className="text-4xl font-bold mb-10 text-gray-800">Notre Ã‰quipe ðŸ’«</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-10">
          {members.map((member, index) => (
            <motion.div
              key={index}
              className="bg-white rounded-2xl shadow-lg p-6"
              whileHover={{ scale: 1.05 }}
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.2 }}
            >
              <img
                src={member.image}
                alt={member.name}
                className="w-32 h-32 rounded-full mx-auto mb-4 object-cover"
              />
              <h3 className="text-xl font-semibold text-gray-700">{member.name}</h3>
              <p className="text-gray-500">{member.role}</p>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default TeamSection;
