import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import po from "../images/po.jpg";
import img from "../images/img.png";

const images = [img, po];

export function Component() {
  const [current, setCurrent] = useState(0);

  const nextSlide = () => {
    setCurrent((prev) => (prev === images.length - 1 ? 0 : prev + 1));
  };

  const prevSlide = () => {
    setCurrent((prev) => (prev === 0 ? images.length - 1 : prev - 1));
  };

  return (
    <div className="relative w-full max-w-4xl mx-auto h-64 sm:h-80 md:h-96 overflow-hidden rounded-2xl shadow-lg">
      <AnimatePresence>
        <motion.img
          key={images[current]}
          src={images[current]}
          alt="carousel"
          initial={{ opacity: 0, x: 100 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: -100 }}
          transition={{ duration: 0.5 }}
          className="w-full h-full object-cover"
        />
      </AnimatePresence>
      <button onClick={prevSlide} className="absolute top-1/2 left-4 transform -translate-y-1/2 bg-white p-2 rounded-full shadow hover:bg-gray-100">
        ◀
      </button>
      <button onClick={nextSlide} className="absolute top-1/2 right-4 transform -translate-y-1/2 bg-white p-2 rounded-full shadow hover:bg-gray-100">
        ▶
      </button>
    </div>
  );
}
