import React from "react";
import '../style/Contact.css';
import po from '..//po.jpg';

export default function ContactForm() {
  return (
    <div className="contact-container">

      {/* Image d’en-tête */}
      <div className="contact-banner">
        <img
          src={po} 
          alt="Paysage"
        />
      </div>

      {/* Titre */}
      <h1 className="contact-title">Contact</h1>

      {/* Sous-titre */}
      <div className="contact-subtitle">
        <h2>Un souci avec ta dernière commande ?</h2>
        <p>
          La team Nôka Matcha répond ici et à{" "}
          <a href="mailto:contact@nokamatcha.com">contact@nokamatcha.com</a>.
          Si ta demande concerne une commande,{" "}
          <strong>renseigne le numéro de commande dans l'onglet \"Message\"</strong>
          {" "}c'est indispensable <span className="heart">♥</span>
        </p>
      </div>

      {/* Formulaire */}
      <form className="contact-form">
        <p className="mandatory-note">*Mentions obligatoires</p>

        <label htmlFor="name">Nom*</label>
        <input id="name" type="text" required />

        <label htmlFor="message">Message</label>
        <textarea id="message" rows="6"></textarea>

        <button type="submit">ENVOYER</button>
      </form>

    </div>
  );
}
